var inp = msg.payload

// get id from flow variable => default 0
var id = flow.get("id") || 0

// increase id by 1
id ++

// set customer id to id
inp.id = id

// set flow variable to id
flow.set("id", id)

// Table command
msg.payload = {
    command : "addRow",
    arguments : [
        [inp],
        true
    ]
}

return msg;

// set inp to msg.payload
const inp = msg.payload

// read selectedRow
var row = flow.get("selectedRow")

// update data
row.name = inp.name
row.age = inp.age


// set command
msg.payload = {
    command : "updateOrAddData",
    arguments : [
        [row]
    ]
}
return msg;

// get selectedRow
const row = flow.get("selectedRow")

// define command
msg.payload = {
    command : "deleteRow",
    arguments : [row.id]
}
return msg;

// get filter obj
const filter = flow.get("filter");

// define payload
msg.payload = {
    command: "setFilter",
    arguments: [
        filter.field,
        filter.type,
        filter.value
    ]
}
return msg;
